<?phpclass userModel extends Model {    public function __construct() {        parent::__construct();    }    public function consult($condicion = '1') {             $data = $this->_db->query("SELECT `id`, `name`, `login`, `password`, `token`, `email`, `img`, `contact`, `state`, `created`, `update`, `rol_id` FROM `user` WHERE " . $condicion);        if ($data)         return $data->fetchall();    }    public function token($id,$token){         $this->_db->prepare("UPDATE user set token='".$token."' where id=".$id)              ->execute();    }    public function createshop($datos) {        $this->_db->prepare("INSERT INTO `user`( `name`, `token`,`email`,`contact`, `state`, `rol_id`) VALUES ( :name, :token, :email, :contact, :state, :rol_id)")                  ->execute($datos);    }    public function actualizar($key, $datos) {}}?>